/// This class defines the interface for the tabulation of the cell
/// tensor corresponding to the local contribution to a form from
/// the integral over a cell.

class poisson_cell_integral_0_otherwise: public ufc::cell_integral
{
public:

  /// Constructor
  poisson_cell_integral_0_otherwise() : ufc::cell_integral()
  {
    // Do nothing
  }

  /// Destructor
  virtual ~poisson_cell_integral_0_otherwise()
  {
    // Do nothing
  }

  /// Tabulate the tensor for the contribution from a local cell
  virtual void tabulate_tensor(double*  A,
                               const double * const *  w,
                               const double*  vertex_coordinates,
                               int cell_orientation) const
  {
    // Number of operations (multiply-add pairs) for Jacobian data:      3
    // Number of operations (multiply-add pairs) for geometry tensor:    31
    // Number of operations (multiply-add pairs) for tensor contraction: 23
    // Total number of operations (multiply-add pairs):                  57
    
    // Compute Jacobian
    double J[9];
    compute_jacobian_tetrahedron_3d(J, vertex_coordinates);
    
    // Compute Jacobian inverse and determinant
    double K[9];
    double detJ;
    compute_jacobian_inverse_tetrahedron_3d(K, detJ, J);
    
    // Set scale factor
    const double det = std::abs(detJ);
    
    // Compute geometry tensor
    const double G0_0_0_0 = det*(w[0][0]*((K[0]*K[0] + K[1]*K[1] + K[2]*K[2])));
    const double G0_0_0_1 = det*(w[0][0]*((K[0]*K[3] + K[1]*K[4] + K[2]*K[5])));
    const double G0_0_0_2 = det*(w[0][0]*((K[0]*K[6] + K[1]*K[7] + K[2]*K[8])));
    const double G0_0_1_0 = det*(w[0][0]*((K[3]*K[0] + K[4]*K[1] + K[5]*K[2])));
    const double G0_0_1_1 = det*(w[0][0]*((K[3]*K[3] + K[4]*K[4] + K[5]*K[5])));
    const double G0_0_1_2 = det*(w[0][0]*((K[3]*K[6] + K[4]*K[7] + K[5]*K[8])));
    const double G0_0_2_0 = det*(w[0][0]*((K[6]*K[0] + K[7]*K[1] + K[8]*K[2])));
    const double G0_0_2_1 = det*(w[0][0]*((K[6]*K[3] + K[7]*K[4] + K[8]*K[5])));
    const double G0_0_2_2 = det*(w[0][0]*((K[6]*K[6] + K[7]*K[7] + K[8]*K[8])));
    
    // Compute element tensor
    A[1] = -0.166666666666667*G0_0_0_0 - 0.166666666666667*G0_0_1_0 - 0.166666666666667*G0_0_2_0;
    A[6] = 0.166666666666667*G0_0_0_1;
    A[14] = 0.166666666666667*G0_0_2_1;
    A[0] = -A[1] + 0.166666666666667*G0_0_0_1 + 0.166666666666667*G0_0_0_2 + 0.166666666666667*G0_0_1_1 + 0.166666666666667*G0_0_1_2 + 0.166666666666667*G0_0_2_1 + 0.166666666666667*G0_0_2_2;
    A[15] = 0.166666666666667*G0_0_2_2;
    A[13] = 0.166666666666667*G0_0_2_0;
    A[12] = -A[13] - 0.166666666666667*G0_0_2_1 - 0.166666666666667*G0_0_2_2;
    A[9] = 0.166666666666667*G0_0_1_0;
    A[5] = 0.166666666666667*G0_0_0_0;
    A[8] = -A[9] - 0.166666666666667*G0_0_1_1 - 0.166666666666667*G0_0_1_2;
    A[7] = 0.166666666666667*G0_0_0_2;
    A[11] = 0.166666666666667*G0_0_1_2;
    A[10] = 0.166666666666667*G0_0_1_1;
    A[4] = -A[6] - 0.166666666666667*G0_0_0_0 - 0.166666666666667*G0_0_0_2;
    A[3] = -A[15] - 0.166666666666667*G0_0_0_2 - 0.166666666666667*G0_0_1_2;
    A[2] = -A[6] - 0.166666666666667*G0_0_1_1 - 0.166666666666667*G0_0_2_1;
  }

};

